import React, { useEffect, useRef } from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;700;900&family=Inter:wght@300;400;600;700&display=swap');
  
  body {
    margin: 0;
    padding: 0;
    font-family: 'Inter', sans-serif;
    -webkit-font-smoothing: antialiased;
    background: #fafafa;
  }
`;

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const Body = styled.div`
  min-height: 100vh;
  color: #2d2d2d;
  font-family: 'Lato', Arial, sans-serif;
  position: relative;
  overflow-x: hidden;
  background: linear-gradient(120deg, #f7f4ef 0%, #e8e3d9 100%);
  &::before {
    content: '';
    position: fixed;
    inset: 0;
    z-index: -1;
    background: linear-gradient(120deg, #f7f4ef 0%, #e8e3d9 100%);
    animation: ${gradientMove} 8s ease-in-out infinite alternate;
  }
`;

const Hero = styled.section`
  background: url('https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1200&q=80') center/cover no-repeat;
  min-height: 60vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: relative;
  overflow: hidden;
  &::after {
    content: '';
    position: absolute;
    inset: 0;
    background: rgba(40, 60, 80, 0.55);
  }
`;

const HeroContent = styled.div`
  position: relative;
  z-index: 1;
  text-align: center;
  color: #fff;
  padding: 60px 20px 40px 20px;
  opacity: 0;
  transform: translateY(30px);
  animation: ${fadeInHero} 1.2s ease-out 0.2s forwards;
`;

const Title = styled.h1`
  font-family: 'Playfair Display', serif;
  font-size: 2.5rem;
  margin-bottom: 1rem;
  font-weight: 900;
  letter-spacing: -1px;
`;

const Subtitle = styled.h2`
  font-family: 'Playfair Display', serif;
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  font-weight: 700;
`;

const Section = styled.section`
  max-width: 600px;
  margin: 40px auto;
  padding: 0 20px;
  opacity: 0;
  transform: translateY(40px);
  transition: opacity 0.8s, transform 0.8s;
  &.visible {
    opacity: 1;
    transform: none;
  }
  p {
    font-size: 1.15rem;
    line-height: 1.7;
    margin-bottom: 1.2em;
  }
`;

const EbookMockup = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 40px 0 20px 0;
  opacity: 0;
  transform: translateY(40px);
  transition: opacity 0.8s, transform 0.8s;
  &.visible {
    opacity: 1;
    transform: none;
  }
  img {
    width: 180px;
    height: auto;
    box-shadow: 0 8px 32px rgba(44, 44, 44, 0.18);
    border-radius: 12px;
    background: #e0e0e0;
  }
`;

const EbookCaption = styled.div`
  margin-top: 12px;
  font-size: 1.1rem;
  color: #f56342;
  font-weight: bold;
`;

const CTAButton = styled.a`
  display: block;
  width: 100%;
  max-width: 400px;
  margin: 40px auto 0 auto;
  padding: 20px;
  background: #f56342;
  color: #fff;
  font-size: 1.35rem;
  text-align: center;
  border-radius: 12px;
  text-decoration: none;
  font-weight: bold;
  box-shadow: 0 4px 16px rgba(245,99,66,0.08);
  transition: background 0.2s, transform 0.2s, box-shadow 0.2s;
  position: relative;
  &:hover, &:focus {
    background: #d94d2a;
    transform: scale(1.05);
    box-shadow: 0 0 24px 4px #f5634266;
  }
`;

const Footer = styled.footer`
  text-align: center;
  color: #888;
  font-size: 1rem;
  margin: 60px 0 20px 0;
  letter-spacing: 0.2px;
  .heart {
    color: #f56342;
    font-size: 1.2em;
    vertical-align: middle;
    margin-left: 4px;
  }
`;

const sectionsSelector = ".section, .ebook-mockup";

export default function SalesPage() {
  const sectionsRef = useRef([]);

  useEffect(() => {
    function revealOnScroll() {
      const reveals = document.querySelectorAll(sectionsSelector);
      const windowHeight = window.innerHeight;
      reveals.forEach(el => {
        const elementTop = el.getBoundingClientRect().top;
        if (elementTop < windowHeight - 60) {
          el.classList.add('visible');
        }
      });
    }
    window.addEventListener('scroll', revealOnScroll);
    revealOnScroll();
    return () => window.removeEventListener('scroll', revealOnScroll);
  }, []);

  return (
    <Body>
      <Hero>
        <HeroContent>
          <Title>
            Tem algo dentro de você que ninguém vê...<br />Mas que grita todos os dias.
          </Title>
          <Subtitle>
            Você não está quebrado.<br />
            Você está sobrecarregado de silêncio, peso e memórias que nunca viraram palavras.<br />
            Esse material nasceu para ser seu ponto de virada.
          </Subtitle>
        </HeroContent>
      </Hero>

      <Section className="section">
        <p>
          Por muito tempo, você aprendeu a sobreviver calado.<br /><br />
          Engoliu dias ruins, sorrisos forçados, dores que ninguém entendeu.<br />
          Tentou parecer forte. Tentou parecer normal.<br /><br />
          Mas a verdade?<br /><br />
          A sua mente não é sua inimiga. Ela só tá cansada.<br /><br />
          Este material não é só um e-book.<br />
          É um abraço com palavras.<br />
          É um reencontro com quem você sempre foi, antes de tudo isso.
        </p>
      </Section>

      <EbookMockup className="ebook-mockup">
        {/* Suggestion: Replace with a 3D mockup image for more perceived value */}
        <img src="https://via.placeholder.com/180x260.png?text=E-book+Mockup" alt="Mockup do E-book" />
        <EbookCaption>
          {/* Value anchoring phrase */}
          Você vai pagar R$10 por algo que vale mais que uma sessão de terapia — porque ele fala com a sua dor antes dela virar ferida.<br /><br />
          Você pode começar agora. R$10.
        </EbookCaption>
      </EbookMockup>

      {/* Micro-story and impact question before CTA */}
      <Section className="section">
        <p>
          Uma vez, em uma madrugada, eu chorei no banheiro em silêncio. E desejei que alguém me entendesse sem que eu precisasse explicar. Esse material nasceu ali — onde dói.<br /><br />
          Se você pudesse aliviar esse peso agora, você apertaria um botão?
        </p>
      </Section>

      <CTAButton
        href="https://SEULINKAQUI.com"
        target="_blank"
        rel="noopener noreferrer"
      >
        🔥 Sim, eu quero me reencontrar por R$10 agora
      </CTAButton>
      
      <Section className="section">
        <p>
          Escrito por alguém que já passou pelo mesmo —<br />
          mas escolheu transformar dor em direção.<br /><br />
          Com base em psicologia simbólica,<br />
          experiências reais<br />
          e um convite para você finalmente se escutar de verdade.
        </p>
      </Section>

      <Section className="section">
        <p>
          Você não precisa continuar carregando tudo sozinho.<br /><br />
          Cada página foi escrita para aliviar esse peso.<br />
          Pra lembrar você que sentir não é fraqueza —<br />
          é o começo da cura.<br /><br />
          Não é um curso.<br />
          Não é autoajuda barata.<br /><br />
          É uma conversa direta com a parte sua que só quer respirar de novo.
        </p>
      </Section>

      <CTAButton
        href="https://SEULINKAQUI.com"
        target="_blank"
        rel="noopener noreferrer"
      >
        🔓 Baixar agora por R$10 e aliviar esse peso agora mesmo
      </CTAButton>

      <Section className="section">
        <p>
          Você não chegou até aqui por acaso.<br />
          E talvez esse seja o primeiro passo pra você voltar pra casa —<br />
          dentro de você.<br /><br />
          O link está aqui.<br />
          A resposta que você tanto buscava, também.
        </p>
      </Section>

      <Footer>
        ⚠️ Produto digital. Entrega imediata por e-mail.<br />
        Você vai receber o PDF e um bônus surpresa exclusivo.
        <span className="heart">♥</span>
      </Footer>
    </Body>
  );
}
