import React, { useEffect, useRef } from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Fraunces:wght@300;400;600;700;900&family=Montserrat:wght@300;400;500;600&display=swap');
  
  body {
    margin: 0;
    padding: 0;
    font-family: 'Montserrat', sans-serif;
    -webkit-font-smoothing: antialiased;
    background: #FFFFFF;
    color: #1A2B3C;
    line-height: 1.8;
    text-align: center;
  }

  * {
    box-sizing: border-box;
  }
`;

const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(to right, #FFFFFF, #F8F9FA);
`;

const Hero = styled.section`
  background: linear-gradient(rgba(26, 43, 60, 0.75), rgba(26, 43, 60, 0.85)),
              url('https://images.unsplash.com/photo-1536152470836-b943b246224c?auto=format&fit=crop&w=2000&q=80') center/cover no-repeat;
  min-height: 85vh;
  display: flex;
  align-items: center;
  position: relative;
  padding: 3rem 1.5rem;
`;

const HeroContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  text-align: center;
  color: #FFFFFF;
  opacity: 0;
  animation: ${fadeIn} 1.2s ease-out forwards;
`;

const Title = styled.h1`
  font-family: 'Playfair Display', serif;
  font-size: clamp(2.8rem, 6vw, 4.2rem);
  line-height: 1.1;
  margin-bottom: 2.5rem;
  font-weight: 900;
  letter-spacing: -0.03em;
`;

const Subtitle = styled.h2`
  font-family: 'Source Sans Pro', sans-serif;
  font-size: clamp(1.3rem, 2.5vw, 1.6rem);
  font-weight: 300;
  line-height: 1.6;
  margin-bottom: 3rem;
  opacity: 0.95;
  max-width: 90%;
  margin-left: auto;
  margin-right: auto;
`;

const Section = styled.section`
  max-width: 720px;
  margin: 100px auto;
  padding: 0 2rem;
  text-align: center;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }

  p {
    font-family: 'Fraunces', serif;
    font-size: 1.4rem;
    line-height: 1.7;
    color: #2C3E50;
    margin-bottom: 2.5em;
    font-weight: 300;
    letter-spacing: -0.01em;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
  }
`;

const EbookPreview = styled.div`
  margin: 80px auto;
  max-width: 340px;
  perspective: 1500px;
  text-align: center;
  
  .ebook-cover {
    width: 100%;
    aspect-ratio: 1/1.414;
    background: linear-gradient(135deg, #2D5A7C 0%, #1A2B3C 100%);
    border-radius: 8px;
    padding: 2.5rem;
    color: white;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    text-align: center;
    transform: rotateY(-15deg) rotateX(5deg);
    box-shadow: 25px 25px 50px rgba(0, 0, 0, 0.15);
    transition: all 0.5s ease;
    
    h3 {
      font-family: 'Fraunces', serif;
      font-size: 3rem;
      margin-bottom: 1.5rem;
      line-height: 1.1;
      font-weight: 900;
      letter-spacing: -0.02em;
    }
    
    p {
      font-family: 'Montserrat', sans-serif;
      font-size: 1.2rem;
      opacity: 0.9;
      font-weight: 400;
      letter-spacing: 0.3px;
      max-width: 85%;
      margin: 0 auto;
      line-height: 1.6;
    }
  }
`;

const CTAButton = styled.a`
  display: block;
  width: 100%;
  max-width: 420px;
  margin: 3rem auto;
  padding: 1.75rem 2.5rem;
  background: linear-gradient(135deg, #2D5A7C 0%, #1A2B3C 100%);
  color: white;
  font-family: 'Montserrat', sans-serif;
  font-size: 1.35rem;
  text-align: center;
  border-radius: 12px;
  text-decoration: none;
  font-weight: 600;
  letter-spacing: 0.5px;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
  box-shadow: 0 15px 30px rgba(26, 43, 60, 0.15);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 20px 40px rgba(26, 43, 60, 0.25);
  }
`;

const SecurePayment = styled.div`
  text-align: center;
  color: #445566;
  font-size: 0.9rem;
  margin-top: 1rem;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
`;

const Footer = styled.footer`
  text-align: center;
  padding: 3rem 1rem;
  color: #445566;
  background: #F8F9FA;
  border-top: 1px solid #E9ECEF;
`;

export default function SalesPage() {
  const sectionsRef = useRef([]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add('visible');
          }
        });
      },
      { threshold: 0.1 }
    );

    document.querySelectorAll('.section').forEach(section => {
      observer.observe(section);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <>
      <GlobalStyle />
      <Container>
        <Hero>
          <HeroContent>
            <Title>
              Transforme seu silêncio em força<br />e suas dores em sabedoria
            </Title>
            <Subtitle>
              Um guia profundo para reconectar-se com sua essência<br />
              e descobrir a força que sempre esteve dentro de você
            </Subtitle>
          </HeroContent>
        </Hero>

        <Section className="section">
          <p>
            Você carrega histórias que ninguém conhece.<br />
            Sentimentos que parecem impossíveis de explicar.<br />
            Uma força que, às vezes, você mesmo duvida que existe.
          </p>
          <p>
            Este não é apenas mais um e-book.<br />
            É um caminho de autodescoberta e transformação.
          </p>
        </Section>

        <EbookPreview>
          <div className="ebook-cover">
            <h3>Você Importa</h3>
            <p>Encontrando Sentido e Alívio no Sofrimento Humano</p>
          </div>
        </EbookPreview>

        <CTAButton href="#comprar">
          Comece Sua Jornada Agora
        </CTAButton>

        <SecurePayment>
          <svg width="16" height="16" viewBox="0 0 16 16" fill="#445566">
            <path d="M13 5l-1-1V2c0-1.1-.9-2-2-2H6C4.9 0 4 .9 4 2v2L3 5c-.6.6-1 1.4-1 2.3V13c0 1.7 1.3 3 3 3h6c1.7 0 3-1.3 3-3V7.3c0-.9-.4-1.7-1-2.3zM6 2h4v2H6V2z"/>
          </svg>
          Pagamento 100% Seguro • Acesso Imediato
        </SecurePayment>

        <Footer>
          © 2024 Todos os direitos reservados
        </Footer>
      </Container>
    </>
  );
}
