import React, { useEffect, useRef } from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";

// Adicione a anima√ß√£o gradientMove que estava faltando
const gradientMove = keyframes`
  0% { background-position: 0% 50%; }
  100% { background-position: 100% 50%; }
`;

const fadeInHero = keyframes`
  to {
    opacity: 1;
    transform: none;
  }
`;

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Playfair+Display:wght@400;700;900&family=Inter:wght@300;400;600;700&display=swap');
  
  body {
    margin: 0;
    padding: 0;
    font-family: 'Inter', sans-serif;
    -webkit-font-smoothing: antialiased;
    background: #fafafa;
  }
`;

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const Body = styled.div`
  min-height: 100vh;
  color: #2d2d2d;
  font-family: 'Lato', Arial, sans-serif;
  position: relative;
  overflow-x: hidden;
  background: linear-gradient(120deg, #f7f4ef 0%, #e8e3d9 100%);
  &::before {
    content: '';
    position: fixed;
    inset: 0;
    z-index: -1;
    background: linear-gradient(120deg, #f7f4ef 0%, #e8e3d9 100%);
    animation: ${gradientMove} 8s ease-in-out infinite alternate;
  }
`;

const Hero = styled.section`
  background: url('https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1200&q=80') center/cover no-repeat;
  min-height: 60vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  position: relative;
  overflow: hidden;
  &::after {
    content: '';
    position: absolute;
    inset: 0;
    background: rgba(40, 60, 80, 0.55);
  }
`;

const HeroContent = styled.div`
  position: relative;
  z-index: 1;
  text-align: center;
  color: #fff;
  padding: 60px 20px 40px 20px;
  opacity: 0;
  transform: translateY(30px);
  animation: ${fadeInHero} 1.2s ease-out 0.2s forwards;
`;

const Title = styled.h1`
  font-family: 'Playfair Display', serif;
  font-size: 2.5rem;
  margin-bottom: 1rem;
  font-weight: 900;
  letter-spacing: -1px;
`;

const Subtitle = styled.h2`
  font-family: 'Playfair Display', serif;
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  font-weight: 700;
`;

const Section = styled.section`
  max-width: 600px;
  margin: 40px auto;
  padding: 0 20px;
  opacity: 0;
  transform: translateY(40px);
  transition: opacity 0.8s, transform 0.8s;
  &.visible {
    opacity: 1;
    transform: none;
  }
  p {
    font-size: 1.15rem;
    line-height: 1.7;
    margin-bottom: 1.2em;
  }
`;

const EbookMockup = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 40px 0 20px 0;
  opacity: 0;
  transform: translateY(40px);
  transition: opacity 0.8s, transform 0.8s;
  &.visible {
    opacity: 1;
    transform: none;
  }
  img {
    width: 180px;
    height: auto;
    box-shadow: 0 8px 32px rgba(44, 44, 44, 0.18);
    border-radius: 12px;
    background: #e0e0e0;
  }
`;

const EbookCaption = styled.div`
  margin-top: 12px;
  font-size: 1.1rem;
  color: #f56342;
  font-weight: bold;
`;

const CTAButton = styled.a`
  display: block;
  width: 100%;
