import React, { useState, useEffect } from "react";
import styled, { createGlobalStyle } from "styled-components";
import ebookCapa from './images/ebook.png'; // Importando a capa do ebook

// Imagem de marca d'água
const watermarkUrl = 'https://images.unsplash.com/photo-1535930749574-1399327ce78f?ixlib=rb-4.0.3&auto=format&fit=crop&w=1600&q=80';

const GlobalStyle = createGlobalStyle`
  * {
    box-sizing: border-box;
  }

  html, body {
    margin: 0;
    padding: 0;
    height: 100%;
    font-family: 'Segoe UI', sans-serif;
    background: #f7f7f7;
    color: #333;
    position: relative;
  }

  #root {
    position: relative;
    z-index: 1;
  }
`;

const Watermark = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background: url(${watermarkUrl}) center center no-repeat;
  background-size: cover;
  opacity: 0.05;
  z-index: 0;
  pointer-events: none;
`;

const Wrapper = styled.div`
  position: relative;
  z-index: 1;
`;

const Hero = styled.section`
  background: url('/img/bg.jpg') center center / cover no-repeat;
  text-align: center;
  color: white;
  padding: 120px 20px 40px;
  position: relative;

  h1 {
    font-size: 36px;
    font-weight: 800;
    margin-bottom: 20px;
    line-height: 1.4;
    color: #000;

    span {
      color: #ff3e6c;
      text-decoration: underline;
      text-decoration-color: #ff3e6c; /* Cor do sublinhado */
      text-decoration-thickness: 3px; /* Espessura do sublinhado */
      text-underline-offset: 4px; /* Distância entre o texto e o sublinhado */
    }
  }

  h2 {
    font-size: 22px;
    font-weight: 400;
    margin-bottom: 30px;
    max-width: 700px;
    margin-left: auto;
    margin-right: auto;
    line-height: 1.6;
    color: #000;
  }

  iframe {
    width: 100%;
    max-width: 560px;
    height: 315px;
    border-radius: 12px;
    box-shadow: 0 8px 20px rgba(0,0,0,0.3);
