import React, { useEffect, useRef } from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Fraunces:wght@300;400;600;700;900&family=Montserrat:wght@300;400;500;600&display=swap');
  
  body {
    margin: 0;
    padding: 0;
    font-family: 'Montserrat', sans-serif;
    -webkit-font-smoothing: antialiased;
    background: #FFFFFF;
    color: #1A2B3C;
    line-height: 1.8;
    text-align: center;
  }

  * {
    box-sizing: border-box;
  }
`;

const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(to right, #FFFFFF, #F8F9FA);
`;

const Hero = styled.section`
  background: linear-gradient(rgba(26, 43, 60, 0.75), rgba(26, 43, 60, 0.85)),
              url('https://images.unsplash.com/photo-1536152470836-b943b246224c?auto=format&fit=crop&w=2000&q=80') center/cover no-repeat;
  min-height: 85vh;
  display: flex;
  align-items: center;
  position: relative;
  padding: 3rem 1.5rem;
`;

const HeroContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  text-align: center;
  color: #FFFFFF;
  opacity: 0;
  animation: ${fadeIn} 1.2s ease-out forwards;
`;

const Title = styled.h1`
  font-family: 'Playfair Display', serif;
  font-size: clamp(2.8rem, 6vw, 4.2rem);
  line-height: 1.1;
  margin-bottom: 2.5rem;
  font-weight: 900;
  letter-spacing: -0.03em;
`;

const Subtitle = styled.h2`
  font-family: 'Fraunces', serif;
  font-size: clamp(1.4rem, 2.5vw, 1.8rem);
  font-weight: 400;
  line-height: 1.6;
  margin-bottom: 3rem;
  opacity: 0.95;
  max-width: 85%;
  margin-left: auto;
  margin-right: auto;
`;

const EmotionalBlock = styled.div`
  font-family: 'Fraunces', serif;
  font-size: 1.5rem;
  line-height: 1.8;
  color: #2C3E50;
  max-width: 720px;
  margin: 80px auto;
  padding: 0 2rem;
  text-align: center;

  p {
    margin-bottom: 2em;
    font-weight: 300;
  }

  .emphasis {
    font-weight: 500;
    color: #1A2B3C;
  }
`;

const Price = styled.div`
  font-family: 'Montserrat', sans-serif;
  font-size: 1.8rem;
  color: #2D5A7C;
  margin: 2rem 0;
  font-weight: 600;
`;

const Section = styled.section`
  max-width: 720px;
  margin: 100px auto;
  padding: 0 2rem;
  text-align: center;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }

  p {
    font-family: 'Fraunces', serif;
    font-size: 1.4rem;
    line-height: 1.7;
    color: #2C3E50;
    margin-bottom: 2.5em;
    font-weight: 300;
    letter-spacing: -0.01em;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
  }
`;

const EbookPreview = styled.div`
  margin: 80px auto;
  max-width: 340px;
  perspective: 1500px;
  text-align: center;
  
  .ebook-cover {
    width: 100%;
    aspect-ratio: 1/1.414;
    background: linear-gradient(135deg, #2D5A7C 0%, #1A2B3C 100%);
    border-radius: 8px;
    padding: 2.5rem;
    color: white;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    text-align: center;
    transform: rotateY(-15deg) rotateX(5deg);
    box-shadow: 25px 25px 50px rgba(0, 0, 0, 0.15);
    transition: all 0.5s ease;
    
    h3 {
