import React, { useEffect, useRef, useState } from "react";
import styled, { keyframes, createGlobalStyle } from "styled-components";
import { Helmet } from 'react-helmet';

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const GlobalStyle = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css2?family=Fraunces:wght@300;400;600;700;900&family=Montserrat:wght@300;400;500;600&display=swap');
  
  body {
    margin: 0;
    padding: 0;
    font-family: 'Montserrat', sans-serif;
    -webkit-font-smoothing: antialiased;
    background: #FFFFFF;
    color: #1A2B3C;
    line-height: 1.8;
    text-align: center;
  }

  * {
    box-sizing: border-box;
  }
`;

const Container = styled.div`
  font-family: 'Arial', sans-serif;
  line-height: 1.6;
  color: #333;
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  text-align: center;
`;

const HeroSection = styled.section`
  background: #f9f9f9;
  padding: 40px 20px;
  border-radius: 8px;
  margin-bottom: 20px;
`;

const Title = styled.h1`
  font-size: 2.5rem;
  color: #222;
  margin-bottom: 20px;
`;

const Subtitle = styled.h2`
  font-size: 1.5rem;
  color: #555;
  margin-bottom: 30px;
`;

const EbookImage = styled.img`
  max-width: 100%;
  height: auto;
  margin: 20px 0;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const CTAButton = styled.button`
  background: #007BFF;
  color: white;
  border: none;
  padding: 15px 30px;
  font-size: 1.2rem;
  border-radius: 5px;
  cursor: pointer;
  transition: background 0.3s;

  &:hover {
    background: #0056b3;
  }
`;

const Section = styled.section`
  margin-bottom: 40px;
  text-align: left;
`;

const SectionTitle = styled.h3`
  font-size: 1.8rem;
  color: #222;
  margin-bottom: 20px;
`;

const Paragraph = styled.p`
  font-size: 1.2rem;
  margin-bottom: 20px;
`;

const List = styled.ul`
  list-style: none;
  padding: 0;
  margin-bottom: 20px;
`;

const ListItem = styled.li`
  font-size: 1.2rem;
  margin-bottom: 10px;
  &::before {
    content: '‚úîÔ∏è';
    margin-right: 10px;
    color: #007BFF;
  }
`;

const FAQ = styled.div`
  margin-top: 40px;
  text-align: left;
`;

const FAQItem = styled.div`
  margin-bottom: 20px;
`;

const Question = styled.h4`
  font-size: 1.2rem;
  color: #222;
  margin-bottom: 10px;
`;

const Answer = styled.p`
  font-size: 1rem;
  color: #555;
`;

const GuaranteeSection = styled.section`
  background: #f0f8ff;
  border: 2px solid #007BFF;
  border-radius: 10px;
  padding: 30px;
  margin-bottom: 40px;
  text-align: center;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const GuaranteeTitle = styled.h3`
  font-size: 2rem;
  color: #007BFF;
  margin-bottom: 20px;
  font-weight: bold;
`;

const GuaranteeText = styled.p`
  font-size: 1.2rem;
  color: #333;
  margin-bottom: 20px;
  line-height: 1.8;
`;

const FAQSection = styled.section`
  background: #f9f9f9;
  border-radius: 10px;
  padding: 30px;
  margin-bottom: 40px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const FAQTitle = styled.h3`
  font-size: 2rem;
  color: #222;
  margin-bottom: 20px;
  font-weight: bold;
`;

const FAQItemStyled = styled.div`
  margin-bottom: 20px;
`;

const FAQQuestion = styled.h4`
  font-size: 1.2rem;
  color: #007BFF;
  margin-bottom: 10px;
