$RefreshReg$(_c6, "PriceSection");
$RefreshReg$(_c7, "ContentSection");
$RefreshReg$(_c8, "Benefits");
$RefreshReg$(_c9, "UrgencySection");
$RefreshReg$(_c10, "WhyNowSection");
$RefreshReg$(_c11, "BioSection");
$RefreshReg$(_c12, "ProofsSection");
$RefreshReg$(_c13, "MetricsSection");
